import 'package:flutter/material.dart';/// TicketState enumenum TicketState {  ready("Kullanıma Hazır", Colors.orange, Icons.timelapse),  used("Kullanıldı", Colors.green, Icons.check_circle),  expired("Süresi Doldu", Colors.purpleAccent, Icons.error),  canceled("İptal Edildi", Colors.red, Icons.cancel)  ;  const TicketState(this.title,this.color,this.icon);  /// TicketState degeri  final String title;  final Color color;  final IconData icon;  /// name to TicketState  static TicketState fromName(String name) {    switch (name) {      case 'ready':        return TicketState.ready;      case 'used':        return TicketState.used;      case 'expired':        return TicketState.expired;      case 'canceled':        return TicketState.canceled;      default:        return TicketState.ready;    }  }}