import 'package:firebase_auth/firebase_auth.dart';import 'package:firebase_database/firebase_database.dart';import 'package:online_ticked/core/constants/app_referance.dart';class AuthService {  AuthService._();  static AuthService? _instance;  static AuthService get instance => _instance ??= AuthService._();  Future<void> saveLastLogin(String id) async {    await FirebaseDatabase.instance        .ref(AppReferance.users.value)        .child(id)        .update({'lastLogin': DateTime.now().toIso8601String()});  }  Future<void> login({required String email, required String password}) async {    await FirebaseAuth.instance        .signInWithEmailAndPassword(      email: email,      password: password,    )        .onError((error, stackTrace) {      throw 'Giriş Başarısız';    });    await saveLastLogin(FirebaseAuth.instance.currentUser!.uid);  }  bool get isAuth => FirebaseAuth.instance.currentUser != null;  String? get id => FirebaseAuth.instance.currentUser?.uid;}