import 'package:flutter/material.dart';import 'package:go_router/go_router.dart';import 'package:online_ticked/core/constants/app_route_names.dart';import 'package:online_ticked/data/services/firebase_auth_service.dart';import 'login_page.dart';/// LoginPage mixin sınıfımixin LoginPageMixin on State<LoginPage> {  bool isLoading = true;  @override  void initState() {    // TODO: implement initState    super.initState();    Future.delayed(const Duration(milliseconds: 500), () {      isLogin();    });  }  void isLogin() {    if (FirebaseAuthService.instance.isSignedIn()) {    GoRouter.of(context).go(AppRouteName.dashboard.path);    }    setState(() {      isLoading = false;    });  }}