import 'package:flutter/material.dart';import 'package:go_router/go_router.dart';import 'package:online_ticked/core/constants/app_asets.dart';import 'package:online_ticked/core/constants/app_route_names.dart';import 'package:online_ticked/data/services/auth_service.dart';import 'package:online_ticked/provider/auth_cubit.dart';import 'package:provider/provider.dart';import 'home_screen_mixin.dart';/// HomeScreen ekranıclass HomeScreen extends StatefulWidget {  /// HomeScreen ekranı  const HomeScreen({super.key});  @override  State<HomeScreen> createState() => _HomeScreenState();}class _HomeScreenState extends State<HomeScreen> with HomeScreenMixin {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: const Text('Çınar SPA Hediye Kartı'),      ),      body: SafeArea(        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          children: [            Image.asset(AppAsets.logo.value, height: 200),            Padding(              padding: const EdgeInsets.all(8.0),              child: FilledButton.icon(                  onPressed: () => context.push(AppRouteName.ticketList.path),                  icon: Icon(Icons.list),                  label: Center(child: Text('Kayıtlı Biletler')),              ),            ),            Padding(              padding: const EdgeInsets.all(8.0),              child: FilledButton.icon(                  onPressed: () => context.push(AppRouteName.verifyTicket.path),                icon: Icon(Icons.check_circle_outline_rounded),                style: ButtonStyle(                    backgroundColor: MaterialStateProperty.all(Colors.orange)),                label: Center(child: Text('Bilet Dogrula'))),            ),            Padding(              padding: const EdgeInsets.all(8.0),              child: FilledButton.icon(                  onPressed: () => context.push(AppRouteName.createTicket.path),                  style: ButtonStyle(                      backgroundColor: MaterialStateProperty.all(Colors.green)),                  icon: Icon(Icons.add),                  label: Center(child: Text('Bilet Oluştur'))),            ),          ],        ),      ),      drawer: Drawer(        child: Column(          children: [            DrawerHeader(              child: Center(                child: Image.asset(AppAsets.logo.value, height: 100),              ),            ),            ListTile(              leading: const Icon(Icons.list),              title: const Text('Kayıtlı Biletler'),              onTap: () => context.push(AppRouteName.ticketList.path),            ),            ListTile(              leading: const Icon(Icons.check_circle_outline_rounded),              title: const Text('Bilet Dogrula'),              onTap: () => context.push(AppRouteName.verifyTicket.path),            ),            ListTile(              leading: const Icon(Icons.add),              title: const Text('Bilet Oluştur'),              onTap: () => context.push(AppRouteName.createTicket.path),            ),            Spacer(),            ListTile(              leading: const Icon(Icons.info),              title: const Text('Uygulama Hakkında'),              onTap: () => showAboutDialog(                context: context,                applicationIcon: Image.asset(AppAsets.logo.value, height: 50),                applicationName: 'Çınar SPA Hediye Kartı Uygulaması',                applicationVersion: '1.0.0',                applicationLegalese: 'FIRESOFT tarafından geliştirilmiştir.',                barrierDismissible: true,                anchorPoint: const Offset(0.5, 0.5),                barrierColor: Colors.black.withOpacity(0.5),                useRootNavigator: false              ),            ),            ListTile(              leading: const Icon(Icons.exit_to_app),              title: const Text('Çıkış Yap'),              onTap: () {               context.read<AuthCubit>().signOut();               context.go(AppRouteName.login.path);              },            )          ],        ),      )    );  }}