import 'package:flutter/material.dart';import 'package:online_ticked/core/constants/ticket_state.dart';import 'package:online_ticked/ui/screens/ticket_list/ticket_list.dart';/// TicketList mixin sınıfımixin TicketListMixin on State<TicketList> {  TicketState? selectedState = null;  void onTicketStateSelected(TicketState? state) {    setState(() {      selectedState = state;    });  }}