import 'package:flutter/material.dart';import 'package:online_ticked/ui/screens/ticket_verification_screen/ticket_verification_screen.dart';import 'package:online_ticked/ui/widgets/qr_scanner.dart';import 'package:online_ticked/util/bildiriler.dart';import 'package:permission_handler/permission_handler.dart';/// TicketVerificationScreen mixin sınıfımixin TicketVerificationScreenMixin on State<TicketVerificationScreen> {  String? id;  @override  void initState() {    // TODO: implement initState    super.initState();    openScanner();  }  void openScanner() async {    final status = await Permission.camera.request();    if (status.isGranted) {      final result = await Navigator.push<String>(        context,        MaterialPageRoute(builder: (context) => const QrScanner()),      );      if (result != null) {        setState(() {          id = result;        });      }    } else {     hataBildiri(context, 'Kamera izni veriniz.');    }  }}