import 'package:flutter/material.dart';import 'package:flutter_pdfview/flutter_pdfview.dart';class PdfPreviewScreen extends StatelessWidget {  final String pdfPath;  PdfPreviewScreen(this.pdfPath);  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(title: Text('Hediye Kartı Önizleme')),      body: PDFView(        filePath: pdfPath,      ),    );  }}